package Lecture1;

public class LoopsDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//       System.out.println("Hello World");
//       System.out.println("Hello World");
//       System.out.println("Hello World");
//       System.out.println("Hello World");
//       System.out.println("Hello World");
		
		for(int i=1;i<=5;i++) {
			System.out.println("Hello World");
		}
		
		int i=1;
		while(i<=5) {	
			System.out.println("Hello World");
			i++;
		}
		
		//i=1;
		do {
			System.out.println("Hello World");
			i++;
		}while(i<=5);
		
		
       
	}

}
